mapper.select:
select sh.*,a.username from studentHomework sh,account a 
where sh.account_id = a.id order by sh.id desc
limit #{page.startNum},#{page.numPerPage}

select count(*) from studentHomework sh,account a where sh.account_id = a.id
and a.username like "%"#{username}"%"
and a.classes = #{serchClass}

select sh.*,a.username from studentHomework sh,account a 
where sh.account_id = a.id 

	and  a.username like "%"#{username}"%"


	and a.classes = #{serchClass}
 
order by sh.id desc
limit #{page.startNum},#{page.numPerPage}

select sh.*,a.username,sum(sh.line) as allLine from studentHomework sh,account a where sh.account_id = a.id
and a.username like "%"#{username}"%"
and a.classes = #{serchClass} GROUP BY sh.account_id order by sh.id desc limit #{page.startNum},#{page.numPerPage}
